import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Camera, X, RotateCcw, Flashlight, FlashlightOff } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import jsQR from 'jsqr';
import { BrowserMultiFormatReader, NotFoundException } from '@zxing/library';

const CameraScanner = ({
  onScanSuccess,
  onError,
  isOpen,
  onClose,
  scanType = 'all' // 'qr', 'barcode', or 'all'
}) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const animationRef = useRef(null);
  const frameCountRef = useRef(0); // Frame counter for optimization

  const [isScanning, setIsScanning] = useState(false);
  const [hasPermission, setHasPermission] = useState(false);
  const [error, setError] = useState(null);
  const [torch, setTorch] = useState(false);
  const [facingMode, setFacingMode] = useState('environment'); // 'user' or 'environment'
  const [scanResult, setScanResult] = useState(null);
  const [scanAttempts, setScanAttempts] = useState(0); // Show user we're trying

  // Real QR Code detection using jsQR library
  const detectQRCode = useCallback((imageData) => {
    try {
      // Try multiple detection methods for better success rate
      let code = null;

      // Try 1: Normal (fastest)
      code = jsQR(imageData.data, imageData.width, imageData.height, {
        inversionAttempts: "dontInvert",
      });

      // Try 2: With inversion (for negative/inverted QR codes)
      if (!code) {
        code = jsQR(imageData.data, imageData.width, imageData.height, {
          inversionAttempts: "attemptBoth",
        });
      }

      if (code && code.data) {
        // Successfully decoded real QR code
        console.log('üìπ Camera detected QR:', code.data);
        return {
          type: 'qr',
          data: code.data, // This is the ACTUAL data from YOUR QR code
          position: code.location
        };
      }

      return null;
    } catch (error) {
      console.error('QR detection error:', error);
      return null;
    }
  }, []);

  // Barcode detection using ZXing
  const detectBarcode = useCallback(async (canvas) => {
    try {
      const codeReader = new BrowserMultiFormatReader();

      // Decode from canvas
      const result = await codeReader.decodeFromCanvas(canvas);

      if (result && result.text) {
        console.log('üìπ Camera detected BARCODE:', result.text);
        console.log('üìπ Barcode format:', result.format);
        return {
          type: 'barcode',
          data: result.text,
          format: result.format
        };
      }

      return null;
    } catch (error) {
      if (!(error instanceof NotFoundException)) {
        console.error('Barcode detection error:', error);
      }
      return null;
    }
  }, []);

  // No mock data generation - using real QR code detection with jsQR

  // Start camera
  const startCamera = useCallback(async () => {
    try {
      setError(null);

      // Check if getUserMedia is available
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('Camera not supported in this browser');
      }

      const constraints = {
        video: {
          facingMode: facingMode,
          width: { ideal: 1280, max: 1920 },
          height: { ideal: 720, max: 1080 }
        }
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        // Add error handler for play
        try {
          await videoRef.current.play();
        } catch (playError) {
          console.warn('Video play warning:', playError);
          // Continue anyway, video might start later
        }
        setHasPermission(true);
        setIsScanning(true);
      }

      // Setup torch if available
      const videoTrack = stream.getVideoTracks()[0];
      if (videoTrack.getCapabilities && videoTrack.getCapabilities().torch) {
        // Torch available
      }

    } catch (err) {
      console.error('Camera error:', err);
      let errorMessage = 'Unable to access camera. ';
      if (err.name === 'NotAllowedError') {
        errorMessage += 'Please allow camera permissions and try again.';
      } else if (err.name === 'NotFoundError') {
        errorMessage += 'No camera found on this device.';
      } else {
        errorMessage += 'Please use the image upload option instead.';
      }
      setError(errorMessage);
      setHasPermission(false);
      if (onError) onError(err);
    }
  }, [facingMode, onError]);

  // Stop camera
  const stopCamera = useCallback(() => {
    // Stop video element first
    if (videoRef.current) {
      videoRef.current.pause();
      videoRef.current.srcObject = null;
    }

    // Then stop tracks
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => {
        track.stop();
      });
      streamRef.current = null;
    }

    // Cancel animation frame
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
    }

    setIsScanning(false);
    setTorch(false);
    setHasPermission(false);
    setScanResult(null); // Clear previous scan result
  }, []);

  // Scan frame
  const scanFrame = useCallback(async () => {
    if (!videoRef.current || !canvasRef.current || !isScanning) {
      return;
    }

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (video.readyState === video.HAVE_ENOUGH_DATA) {
      frameCountRef.current++;

      // Set canvas dimensions to match video
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      // Draw video frame to canvas
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

      // Get image data for QR code processing
      const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);

      let result = null;

      // QR code detection - check every 3 frames (fast)
      if ((scanType === 'qr' || scanType === 'all') && frameCountRef.current % 3 === 0) {
        result = detectQRCode(imageData);
      }

      // Barcode detection - check every 15 frames (slower, more intensive)
      // This prevents "endless scanning" by reducing CPU load
      if (!result && (scanType === 'barcode' || scanType === 'all') && frameCountRef.current % 15 === 0) {
        console.log('üîç Attempting barcode detection... (frame ' + frameCountRef.current + ')');
        setScanAttempts(prev => prev + 1);
        result = await detectBarcode(canvas);
      }

      if (result) {
        setScanResult(result);
        console.log('‚úÖ Code detected by camera:', result);

        // Simulate processing delay
        setTimeout(() => {
          onScanSuccess(result);
          stopCamera();
          onClose();
        }, 500);

        return;
      }
    }

    // Continue scanning
    animationRef.current = requestAnimationFrame(scanFrame);
  }, [isScanning, scanType, detectQRCode, detectBarcode, onScanSuccess, stopCamera, onClose]);

  // Toggle camera facing mode
  const toggleFacingMode = useCallback(() => {
    setFacingMode(prev => prev === 'environment' ? 'user' : 'environment');
  }, []);

  // Toggle torch
  const toggleTorch = useCallback(async () => {
    if (!streamRef.current) return;

    try {
      const videoTrack = streamRef.current.getVideoTracks()[0];
      const capabilities = videoTrack.getCapabilities();

      if (capabilities.torch) {
        await videoTrack.applyConstraints({
          advanced: [{ torch: !torch }]
        });
        setTorch(!torch);
      }
    } catch (err) {
      console.error('Torch error:', err);
    }
  }, [torch]);

  // Effects
  useEffect(() => {
    if (isOpen && hasPermission) {
      startCamera();
    }

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [isOpen, facingMode, hasPermission, startCamera]);

  useEffect(() => {
    if (isScanning) {
      scanFrame();
    }
  }, [isScanning, scanFrame]);

  useEffect(() => {
    if (isOpen) {
      setScanResult(null); // Clear any previous scan result when opening
      setError(null); // Clear any previous errors
      startCamera();
    } else {
      stopCamera();
    }

    return () => {
      stopCamera();
    };
  }, [isOpen, startCamera, stopCamera]);

  if (!isOpen) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 bg-black z-50 flex flex-col"
      >
        {/* Header */}
        <div className="flex items-center justify-between p-4 bg-black/50 text-white">
          <h2 className="text-lg font-semibold">
            Scan {scanType === 'qr' ? 'QR Code' : scanType === 'barcode' ? 'Barcode' : 'Code'}
          </h2>
          <button
            onClick={onClose}
            className="p-2 rounded-full bg-white/10 hover:bg-white/20 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Camera View */}
        <div className="flex-1 relative">
          <video
            ref={videoRef}
            className="w-full h-full object-cover"
            playsInline
            muted
          />

          {/* Hidden canvas for processing */}
          <canvas
            ref={canvasRef}
            className="hidden"
          />

          {/* Scanning Overlay */}
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="relative">
              {/* Scanning Frame */}
              <motion.div
                className="w-64 h-64 border-2 border-white rounded-lg relative"
                initial={{ scale: 0.8 }}
                animate={{ scale: 1 }}
                transition={{ duration: 0.3 }}
              >
                {/* Corner indicators */}
                <div className="absolute top-0 left-0 w-8 h-8 border-l-4 border-t-4 border-blue-500 rounded-tl-lg"></div>
                <div className="absolute top-0 right-0 w-8 h-8 border-r-4 border-t-4 border-blue-500 rounded-tr-lg"></div>
                <div className="absolute bottom-0 left-0 w-8 h-8 border-l-4 border-b-4 border-blue-500 rounded-bl-lg"></div>
                <div className="absolute bottom-0 right-0 w-8 h-8 border-r-4 border-b-4 border-blue-500 rounded-br-lg"></div>

                {/* Scanning line animation */}
                {isScanning && (
                  <motion.div
                    className="absolute left-0 right-0 h-1 bg-gradient-to-r from-transparent via-blue-500 to-transparent"
                    animate={{ y: [0, 248, 0] }}
                    transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
                  />
                )}
              </motion.div>

              {/* Instruction text */}
              <p className="text-white text-center mt-4 text-sm">
                {scanType === 'qr' ? 'Position QR code within the frame' :
                 scanType === 'barcode' ? 'Position barcode within the frame' :
                 'Position QR code or barcode within the frame'}
              </p>

              {/* Scanning status for barcode */}
              {(scanType === 'barcode' || scanType === 'all') && scanAttempts > 0 && (
                <motion.div 
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  className="mt-2 bg-blue-600/80 text-white px-4 py-2 rounded-full text-xs"
                >
                  üîç Scanning... Attempt {scanAttempts}
                  <div className="text-[10px] mt-1">Hold steady for barcode detection</div>
                </motion.div>
              )}
            </div>
          </div>

          {/* Scan result overlay */}
          {scanResult && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              className="absolute inset-0 flex items-center justify-center bg-black/50"
            >
              <div className="bg-white rounded-lg p-6 mx-4 text-center">
                <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Camera className="h-8 w-8 text-green-600" />
                </div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Code Detected!</h3>
                <p className="text-gray-600 mb-4">
                  {scanResult.type.toUpperCase()}: {scanResult.data}
                </p>
                <div className="animate-spin w-6 h-6 border-2 border-blue-600 border-t-transparent rounded-full mx-auto"></div>
              </div>
            </motion.div>
          )}
        </div>

        {/* Controls */}
        <div className="p-4 bg-black/50 flex items-center justify-center space-x-6">
          {/* Torch Toggle */}
          <button
            onClick={toggleTorch}
            className={`p-3 rounded-full transition-colors ${
              torch ? 'bg-yellow-500 text-black' : 'bg-white/10 text-white hover:bg-white/20'
            }`}
          >
            {torch ? <Flashlight className="h-6 w-6" /> : <FlashlightOff className="h-6 w-6" />}
          </button>

          {/* Flip Camera */}
          <button
            onClick={toggleFacingMode}
            className="p-3 rounded-full bg-white/10 text-white hover:bg-white/20 transition-colors"
          >
            <RotateCcw className="h-6 w-6" />
          </button>
        </div>

        {/* Error Message */}
        {error && (
          <motion.div
            initial={{ y: 100 }}
            animate={{ y: 0 }}
            className="absolute bottom-20 left-4 right-4 bg-red-600 text-white p-4 rounded-lg"
          >
            <p className="text-center">{error}</p>
            <button
              onClick={startCamera}
              className="mt-2 w-full bg-white/20 text-white py-2 rounded hover:bg-white/30 transition-colors"
            >
              Try Again
            </button>
          </motion.div>
        )}
      </motion.div>
    </AnimatePresence>
  );
};

export default CameraScanner;
