import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Camera, X, RotateCcw, Flashlight, FlashlightOff } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import jsQR from 'jsqr';
import { BrowserMultiFormatReader, NotFoundException } from '@zxing/library';

const CameraScanner = ({
  onScanSuccess,
  onError,
  isOpen,
  onClose,
  scanType = 'all' // 'qr', 'barcode', or 'all'
}) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const scanningRef = useRef(false);
  const barcodeReaderRef = useRef(null);

  const [hasPermission, setHasPermission] = useState(false);
  const [error, setError] = useState(null);
  const [torch, setTorch] = useState(false);
  const [facingMode, setFacingMode] = useState('environment');
  const [scanResult, setScanResult] = useState(null);
  const [scanAttempts, setScanAttempts] = useState(0);
  const [isProcessing, setIsProcessing] = useState(false);

  // Initialize barcode reader
  useEffect(() => {
    barcodeReaderRef.current = new BrowserMultiFormatReader();
    console.log('‚úÖ Barcode reader initialized');

    return () => {
      if (barcodeReaderRef.current) {
        barcodeReaderRef.current.reset();
      }
    };
  }, []);

  // QR Code detection function
  const detectQRCode = useCallback((imageData) => {
    if (!imageData || !imageData.data) return null;

    try {
      const code = jsQR(imageData.data, imageData.width, imageData.height, {
        inversionAttempts: "attemptBoth",
      });

      if (code && code.data) {
        console.log('‚úÖ QR Code detected:', code.data);
        return {
          type: 'qr',
          data: code.data
        };
      }
    } catch (error) {
      console.error('QR detection error:', error);
    }

    return null;
  }, []);

  // Barcode detection function
  const detectBarcode = useCallback(async (canvas) => {
    if (!canvas || !barcodeReaderRef.current) return null;

    try {
      const result = await barcodeReaderRef.current.decodeFromCanvas(canvas);

      if (result && result.text) {
        console.log('‚úÖ Barcode detected:', result.text, 'Format:', result.format);
        return {
          type: 'barcode',
          data: result.text,
          format: result.format
        };
      }
    } catch (error) {
      if (!(error instanceof NotFoundException)) {
        console.log('Barcode detection attempt:', error.message || 'Not found');
      }
    }

    return null;
  }, []);

  // Main scanning loop
  const scan = useCallback(async () => {
    if (!scanningRef.current || isProcessing) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;

    if (!video || !canvas || video.readyState !== video.HAVE_ENOUGH_DATA) {
      requestAnimationFrame(scan);
      return;
    }

    try {
      const ctx = canvas.getContext('2d');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

      const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
      let result = null;

      // Try QR code detection
      if (scanType === 'qr' || scanType === 'all') {
        result = detectQRCode(imageData);
        if (result) {
          console.log('‚úÖ QR Code found!');
          setIsProcessing(true);
          setScanResult(result);
          setTimeout(() => {
            onScanSuccess(result);
            stopScanning();
            onClose();
          }, 500);
          return;
        }
      }

      // Try barcode detection (less frequently)
      if (!result && (scanType === 'barcode' || scanType === 'all')) {
        setScanAttempts(prev => prev + 1);
        result = await detectBarcode(canvas);

        if (result) {
          console.log('‚úÖ Barcode found!');
          setIsProcessing(true);
          setScanResult(result);
          setTimeout(() => {
            onScanSuccess(result);
            stopScanning();
            onClose();
          }, 500);
          return;
        }
      }

    } catch (error) {
      console.error('Scan error:', error);
    }

    // Continue scanning
    if (scanningRef.current) {
      requestAnimationFrame(scan);
    }
  }, [scanType, detectQRCode, detectBarcode, onScanSuccess, onClose, isProcessing]);

  // Start camera
  const startCamera = useCallback(async () => {
    try {
      console.log('üé• Starting camera...', 'Scan type:', scanType);
      setError(null);
      setScanResult(null);
      setScanAttempts(0);
      setIsProcessing(false);

      if (!navigator.mediaDevices?.getUserMedia) {
        throw new Error('Camera not supported in this browser');
      }

      const constraints = {
        video: {
          facingMode: facingMode,
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        await videoRef.current.play();
        console.log('‚úÖ Camera started, resolution:', videoRef.current.videoWidth, 'x', videoRef.current.videoHeight);
        setHasPermission(true);

        // Start scanning
        scanningRef.current = true;
        requestAnimationFrame(scan);
      }

    } catch (err) {
      console.error('‚ùå Camera error:', err);
      let errorMessage = 'Unable to access camera. ';

      if (err.name === 'NotAllowedError' || err.name === 'PermissionDeniedError') {
        errorMessage += 'Please allow camera permissions in your browser settings.';
      } else if (err.name === 'NotFoundError') {
        errorMessage += 'No camera found on this device.';
      } else {
        errorMessage += 'Please try the image upload option instead.';
      }

      setError(errorMessage);
      setHasPermission(false);
      onError?.(err);
    }
  }, [facingMode, scanType, scan, onError]);

  // Stop scanning
  const stopScanning = useCallback(() => {
    console.log('üõë Stopping camera...');
    scanningRef.current = false;

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }

    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }

    setHasPermission(false);
    setTorch(false);
    setScanResult(null);
    setIsProcessing(false);
  }, []);

  // Toggle camera facing mode
  const toggleFacingMode = useCallback(() => {
    stopScanning();
    setFacingMode(prev => prev === 'environment' ? 'user' : 'environment');
    setTimeout(() => startCamera(), 100);
  }, [stopScanning, startCamera]);

  // Toggle torch
  const toggleTorch = useCallback(async () => {
    if (!streamRef.current) return;

    try {
      const videoTrack = streamRef.current.getVideoTracks()[0];
      const capabilities = videoTrack.getCapabilities();

      if (capabilities.torch) {
        await videoTrack.applyConstraints({
          advanced: [{ torch: !torch }]
        });
        setTorch(!torch);
      }
    } catch (err) {
      console.error('Torch error:', err);
    }
  }, [torch]);

  // Handle open/close
  useEffect(() => {
    if (isOpen) {
      startCamera();
    } else {
      stopScanning();
    }

    return () => {
      stopScanning();
    };
  }, [isOpen, startCamera, stopScanning]);

  if (!isOpen) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 bg-black z-50 flex flex-col"
      >
        {/* Header */}
        <div className="flex items-center justify-between p-4 bg-black/50 text-white">
          <h2 className="text-lg font-semibold">
            Scan {scanType === 'qr' ? 'QR Code' : scanType === 'barcode' ? 'Barcode' : 'Code'}
          </h2>
          <button
            onClick={onClose}
            className="p-2 rounded-full bg-white/10 hover:bg-white/20 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Camera View */}
        <div className="flex-1 relative">
          <video
            ref={videoRef}
            className="w-full h-full object-cover"
            playsInline
            muted
            autoPlay
          />

          <canvas ref={canvasRef} className="hidden" />

          {/* Scanning Overlay */}
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="relative">
              {/* Scanning Frame */}
              <motion.div
                className="w-64 h-64 border-2 border-white rounded-lg relative"
                initial={{ scale: 0.8 }}
                animate={{ scale: 1 }}
                transition={{ duration: 0.3 }}
              >
                {/* Corner indicators */}
                <div className="absolute top-0 left-0 w-8 h-8 border-l-4 border-t-4 border-blue-500 rounded-tl-lg"></div>
                <div className="absolute top-0 right-0 w-8 h-8 border-r-4 border-t-4 border-blue-500 rounded-tr-lg"></div>
                <div className="absolute bottom-0 left-0 w-8 h-8 border-l-4 border-b-4 border-blue-500 rounded-bl-lg"></div>
                <div className="absolute bottom-0 right-0 w-8 h-8 border-r-4 border-b-4 border-blue-500 rounded-br-lg"></div>

                {/* Scanning line animation */}
                {hasPermission && !isProcessing && (
                  <motion.div
                    className="absolute left-0 right-0 h-1 bg-gradient-to-r from-transparent via-blue-500 to-transparent"
                    animate={{ y: [0, 248, 0] }}
                    transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
                  />
                )}
              </motion.div>

              {/* Instruction text */}
              <p className="text-white text-center mt-4 text-sm">
                {scanType === 'qr' ? 'Position QR code within the frame' :
                 scanType === 'barcode' ? 'Position barcode within the frame' :
                 'Position QR code or barcode within the frame'}
              </p>

              {/* Scanning status */}
              {hasPermission && !isProcessing && scanAttempts > 0 && (
                <motion.div 
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  className="mt-2 bg-blue-600/80 text-white px-4 py-2 rounded-full text-xs text-center"
                >
                  üîç Scanning... Hold steady
                  {scanType !== 'qr' && <div className="text-[10px] mt-1">Attempts: {scanAttempts}</div>}
                </motion.div>
              )}
            </div>
          </div>

          {/* Scan result overlay */}
          {scanResult && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              className="absolute inset-0 flex items-center justify-center bg-black/50"
            >
              <div className="bg-white rounded-lg p-6 mx-4 text-center">
                <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Camera className="h-8 w-8 text-green-600" />
                </div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Code Detected!</h3>
                <p className="text-gray-600 mb-4">
                  {scanResult.type.toUpperCase()}: {scanResult.data.substring(0, 30)}...
                </p>
                <div className="animate-spin w-6 h-6 border-2 border-blue-600 border-t-transparent rounded-full mx-auto"></div>
              </div>
            </motion.div>
          )}
        </div>

        {/* Controls */}
        <div className="p-4 bg-black/50 flex items-center justify-center space-x-6">
          <button
            onClick={toggleTorch}
            className={`p-3 rounded-full transition-colors ${
              torch ? 'bg-yellow-500 text-black' : 'bg-white/10 text-white hover:bg-white/20'
            }`}
            title="Toggle Flashlight"
          >
            {torch ? <Flashlight className="h-6 w-6" /> : <FlashlightOff className="h-6 w-6" />}
          </button>

          <button
            onClick={toggleFacingMode}
            className="p-3 rounded-full bg-white/10 text-white hover:bg-white/20 transition-colors"
            title="Flip Camera"
          >
            <RotateCcw className="h-6 w-6" />
          </button>
        </div>

        {/* Error Message */}
        {error && (
          <motion.div
            initial={{ y: 100 }}
            animate={{ y: 0 }}
            className="absolute bottom-20 left-4 right-4 bg-red-600 text-white p-4 rounded-lg"
          >
            <p className="text-center text-sm">{error}</p>
            <button
              onClick={startCamera}
              className="mt-2 w-full bg-white/20 text-white py-2 rounded hover:bg-white/30 transition-colors text-sm"
            >
              Try Again
            </button>
          </motion.div>
        )}
      </motion.div>
    </AnimatePresence>
  );
};

export default CameraScanner;
